import { useState, useEffect, useCallback, useRef } from "react";
import { useParams, useNavigate, useSearchParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Movies from "../components/Movies";
import { movieApi } from "../services/movieApi";
import Pagination from "../components/Pagination";

interface TestPageProps {
  onBack: () => void;
}

function MovieList({ onBack }: TestPageProps) {
  const { category, page: urlPage } = useParams();
  const [searchParams, setSearchParams] = useSearchParams();
  const [currentPage, setCurrentPage] = useState("Dashboard");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalMovies, setTotalMovies] = useState(0);
  const [searchQuery, setSearchQuery] = useState(searchParams.get("q") || "");
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [allMovies, setAllMovies] = useState<any[]>([]);
  const navigate = useNavigate();
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Debounced arama fonksiyonu
  const debouncedSearch = useCallback(
    (query: string) => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }

      searchTimeoutRef.current = setTimeout(async () => {
        if (query.trim()) {
          setIsSearching(true);
          try {
            const results = await movieApi.search(query);
            setSearchResults(results);
            setTotalMovies(results.length);
            setTotalPages(Math.ceil(results.length / 6));
            setPage(1); // Arama yapıldığında ilk sayfaya git
          } catch (err) {
            setError("Arama sırasında bir hata oluştu");
            console.error("Search Error:", err);
          } finally {
            setIsSearching(false);
          }
        } else {
          // Boş arama - tüm listeye geri dön
          setSearchResults([]);
          setSearchParams({});
          // Normal kategori yükleme işlemini tetikle
          window.location.reload();
        }
      }, 300);
    },
    [setSearchParams]
  );

  useEffect(() => {
    if (urlPage) {
      setPage(parseInt(urlPage));
    }
  }, [urlPage]);

  useEffect(() => {
    const fetchMovies = async () => {
      setLoading(true);
      setError(null);
      try {
        let data;
        switch (category) {
          case "popular":
            data = await movieApi.getPopular();
            break;
          case "top-rated":
            data = await movieApi.getTopRated();
            break;
          case "now-playing":
            data = await movieApi.getNowPlaying();
            break;
          case "upcoming":
            data = await movieApi.getUpcoming();
            break;
          case "drama":
            data = await movieApi.getMoviesByType("Drama");
            break;
          case "thriller":
            data = await movieApi.getMoviesByType("Gerilim");
            break;
          case "scifi":
            data = await movieApi.getMoviesByType("Bilim Kurgu");
            break;
          case "fantasy":
            data = await movieApi.getMoviesByType("Fantastik");
            break;
          case "action":
            data = await movieApi.getMoviesByType("Suç");
            break;
          case "trending":
            data = await movieApi.getMoviesByType("Gençlik");
            break;
          default:
            data = await movieApi.getAllMovies();
        }
        const moviesPerPage = 6; // Sayfa başına film sayısı
        const totalPagesCount = Math.ceil(data.length / moviesPerPage);
        setTotalPages(totalPagesCount);
        setTotalMovies(data.length);
        setAllMovies(data);
      } catch (err) {
        setError("Fİlmler yüklenirken bir hata oluştu");
        console.error("API Error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchMovies();
  }, [category, page]);

  useEffect(() => {
    if (category) {
      setCurrentPage("Filmler");
    }
  }, [category]);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [page]);

  // URL'den arama parametresini oku ve ilk yüklemede arama yap
  useEffect(() => {
    const urlQuery = searchParams.get("q");
    if (urlQuery && urlQuery !== searchQuery) {
      setSearchQuery(urlQuery);
      debouncedSearch(urlQuery);
    }
  }, [searchParams, debouncedSearch, searchQuery]);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, []);

  const handlePageChange = (newPage: number) => {
    setPage(newPage);
    if (searchQuery) {
      // Arama sonuçları için URL güncelle
      setSearchParams({ q: searchQuery, page: newPage.toString() });
    } else {
      // Normal kategori için URL güncelle
      navigate(`/movies/${category || "popular"}/${newPage}`);
    }
  };

  // Arama input handler
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const query = e.target.value;
    setSearchQuery(query);

    // URL'yi güncelle
    if (query.trim()) {
      setSearchParams({ q: query });
    } else {
      setSearchParams({});
    }

    // Debounced arama
    debouncedSearch(query);
  };

  // Kategori bazlı client-side filtreleme ileride yeniden eklenecek

  const handleSidebarClick = (label: string) => {
    setCurrentPage(label);

    // Sidebar butonlarına göre URL değiştir
    const urlMap: Record<string, string> = {
      Filmler: "/movies",
      Suç: "/movies/action",
      Drama: "/movies/drama",
      Fantastik: "/movies/fantasy",
      "Bilim Kurgu": "/movies/scifi",
      Aksiyon: "/movies/action",
      Komedi: "/movies/popular", // Komedi için popular
      Korku: "/movies/thriller",
      Gençlik: "/movies/trending",
      Gerilim: "/movies/thriller",
      "Çizgi Roman": "/movies/fantasy",
    };

    const targetUrl = urlMap[label] || "/movies/popular";
    navigate(targetUrl);
  };

  const renderContent = () => {
    switch (currentPage) {
      case "Filmler":
        if (loading) {
          return (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
              <span className="ml-3 text-gray-600">Filmler yükleniyor...</span>
            </div>
          );
        }

        if (error) {
          return (
            <div className="text-center py-8">
              <div className="text-red-500 text-lg mb-4">⚠️ {error}</div>
              <button
                onClick={() => window.location.reload()}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Tekrar Dene
              </button>
            </div>
          );
        }

        return (
          <div>
            {/* Kategori bilgisi */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3">
                {searchQuery
                  ? `"${searchQuery}" için arama sonuçları`
                  : category
                  ? `${category} Filmleri`
                  : "Tüm Filmler"}
              </h3>
              <p className="text-gray-600 mb-4">
                {totalMovies} film bulundu (Sayfa {page} / {totalPages})
              </p>

              {/* Arama input alanı */}
              <div className="mb-4">
                <div className="relative max-w-md">
                  <input
                    type="text"
                    placeholder="Film ara... (başlık, tür, yönetmen, yıl)"
                    value={searchQuery}
                    onChange={handleSearchChange}
                    className="w-full px-4 py-2 pl-10 pr-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    {isSearching ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                    ) : (
                      <svg
                        className="h-4 w-4 text-gray-400"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                        />
                      </svg>
                    )}
                  </div>
                  {searchQuery && (
                    <button
                      onClick={() => {
                        setSearchQuery("");
                        setSearchParams({});
                        setSearchResults([]);
                      }}
                      className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 hover:text-gray-600"
                    >
                      <svg
                        className="h-4 w-4"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M6 18L18 6M6 6l12 12"
                        />
                      </svg>
                    </button>
                  )}
                </div>
              </div>

              {/* Filtre butonları - sadece arama yapılmadığında göster */}
              {!searchQuery && (
                <div className="flex flex-wrap gap-2">
                  <button
                    onClick={() => navigate("/movies")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      !category
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Tümü
                  </button>
                  <button
                    onClick={() => navigate("/movies/drama")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "drama"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Drama
                  </button>
                  <button
                    onClick={() => navigate("/movies/thriller")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "thriller"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Gerilim
                  </button>
                  <button
                    onClick={() => navigate("/movies/scifi")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "scifi"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Bilim Kurgu
                  </button>
                  <button
                    onClick={() => navigate("/movies/fantasy")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "fantasy"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Fantastik
                  </button>
                  <button
                    onClick={() => navigate("/movies/action")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "action"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Aksiyon
                  </button>
                  <button
                    onClick={() => navigate("/movies/trending")}
                    className={`px-3 py-1 rounded-full text-sm ${
                      category === "trending"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-200 text-gray-700"
                    }`}
                  >
                    Trend
                  </button>
                </div>
              )}
            </div>

            {/* Film listesi */}
            <div>
              <Movies
                movies={searchQuery ? searchResults : allMovies}
                loading={searchQuery ? isSearching : loading}
                error={error}
                currentPage={page}
                moviesPerPage={6}
              />
            </div>
            <Pagination
              currentPage={page}
              totalPages={totalPages}
              onPageChange={handlePageChange}
            />
          </div>
        );

      // Diğer sidebar menüleri aynı kalabilir
      case "Suç":
        return (
          <div className="grid grid-cols-6 sm:grid-cols-3 lg:grid-cols-6 gap-6">
            <Movies />
          </div>
        );
      // ... diğer case'ler aynı

      default:
        return (
          <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-2xl font-bold mb-4">Dashboard</h2>
            <p>Ana sayfa içeriği burada...</p>
          </div>
        );
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-white-100 relative">
      {/* Sabit çıkış butonu */}
      <div className="absolute top-4 right-4 z-50">
        <button
          onClick={onBack}
          className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg shadow-lg transition-colors duration-200"
        >
          Çıkış Yap
        </button>
      </div>

      <div className="flex gap-6">
        <Sidebar title="Menü" onItemClick={handleSidebarClick} />
        <div className="flex-1">
          <h1 className="text-3xl font-bold mb-6">Test Sayfası</h1>
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default MovieList;
