import { useEffect, useState } from "react";
import { movieApi } from "../services/movieApi";

type Movie = {
  image: string;
  title: string;
  description: string;
  IMDB_Point: string;
  types: string[];
};

interface MoviesProps {
  movies?: Movie[];
  loading?: boolean;
  error?: string | null;
  currentPage?: number;
  moviesPerPage?: number;
}

export default function Movies({
  movies: propMovies,
  loading: propLoading,
  error: propError,
  currentPage = 1,
  moviesPerPage = 6,
}: MoviesProps) {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Props'tan gelen veriler varsa onları kullan, yoksa kendi state'ini kullan
  const displayMovies = propMovies || movies;
  const displayLoading = propLoading !== undefined ? propLoading : loading;
  const displayError = propError !== undefined ? propError : error;

  useEffect(() => {
    // Eğer props'tan movies geliyorsa, kendi API çağrısını yapma
    if (propMovies) return;

    let cancelled = false;

    async function run() {
      try {
        setLoading(true);
        setError(null);

        const data = query
          ? await movieApi.search(query)
          : await movieApi.getAllMovies();

        if (cancelled) return;
        setMovies(
          data.map((m) => ({
            image: m.image,
            title: m.title,
            description: m.description,
            IMDB_Point: m.IMDB_Point,
            types: m.types,
          }))
        );
      } catch (e) {
        if (cancelled) return;
        setError("Veri alınamadı");
      } finally {
        if (!cancelled) setLoading(false);
      }
    }

    run();
    return () => {
      cancelled = true;
    };
  }, [query, propMovies]);

  // Pagination için filmleri filtrele
  const startIndex = (currentPage - 1) * moviesPerPage;
  const endIndex = startIndex + moviesPerPage;
  const paginatedMovies = displayMovies.slice(startIndex, endIndex);

  return (
    <div>
      {/* Sadece props'tan movies gelmiyorsa arama input'unu göster */}
      {!propMovies && (
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Film, tür, yönetmen ara..."
          className="w-full md:w-96 rounded border px-3 py-2 mb-4"
          aria-label="Ara"
        />
      )}

      {displayLoading && <p>Yükleniyor...</p>}
      {displayError && <p className="text-red-600">{displayError}</p>}
      {!displayLoading && !displayError && displayMovies.length === 0 && (
        <p className="text-gray-600">Sonuç bulunamadı.</p>
      )}

      {/* DÜZELTME: GRID kapsayıcı SU ANDA kartların DOĞRUDAN ebeveyni */}
      <div className="grid grid-cols-6 sm:grid-cols-3 lg:grid-cols-6 gap-6">
        {" "}
        {/* DÜZELTME: Fazladan <div> sarmalayıcısını kaldırdık; map edilen kartlar grid'in direkt çocukları */}
        {!displayLoading &&
          !displayError &&
          paginatedMovies.map((movie, index) => (
            <div
              key={`${movie.title}-${index}`}
              className="bg-white rounded-2xl shadow border border-gray-200 overflow-hidden"
            >
              <img
                src={movie.image}
                alt={movie.title}
                className="w-full h-64 object-cover"
                onError={(e) => {
                  (e.currentTarget as HTMLImageElement).src =
                    "/images/Hulk.jpg";
                }}
                loading="lazy"
              />
              <div className="p-4">
                <div className="flex items-center justify-between mb-2">
                  <h3 className="text-lg font-semibold text-gray-900">
                    {movie.title}
                  </h3>
                  <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                    IMDB {movie.IMDB_Point}
                  </span>
                </div>
                <p className="text-sm text-gray-600 mb-3">
                  {movie.description}
                </p>

                <div className="flex flex-wrap gap-1">
                  {movie.types.map((type) => (
                    <span
                      key={type}
                      className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                    >
                      {type}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
